mapper.select:
SELECT board_type_nm
  FROM board_type
 WHERE board_type_seq = ${type}

SELECT count(*) 
  FROM board
 WHERE board_type_seq = ${type}

mapper.update:
UPDATE board
   SET hit = IFNULL(hit,0) + 1
WHERE board_seq = #{boardSeq}

UPDATE board
   SET title = #{title},
       content = #{content},
       update_dtm = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
 WHERE board_type_seq = ${boardTypeSeq}
   AND board_seq = ${boardSeq}

DELETE FROM board
 WHERE board_type_seq = ${boardTypeSeq}
   AND board_seq = ${boardSeq}
