mapper.update:
UPDATE board_attach
   SET hit = IFNULL(hit,0) + 1
WHERE attach_seq = #{attachSeq}

mapper.select:
SELECT COUNT(1)
  FROM board_attach
 WHERE board_type_seq = #{boardTypeSeq}
   AND board_seq = #{boardSeq}

mapper.delete:
    	DELETE FROM board_attach
	    	  WHERE board_type_seq = #{boardTypeSeq}
	    	    AND board_seq = #{boardSeq}

   	    	 SELECT COUNT(1) 
   	    	   FROM board_attach
	    	  WHERE board_type_seq = #{boardTypeSeq}
	    	    AND board_seq = #{boardSeq}
	    	    AND attach_seq = #{attachSeq}
	    	    AND org_file_nm = #{org_file_nm}
