mapper.select:
SELECT is_like
  FROM board_vote
 WHERE board_type_seq = #{boardTypeSeq}
   AND board_seq = #{boardSeq}
   AND member_id = #{memberId}

mapper.update:
UPDATE board_vote
   SET is_like = #{isLike},
   	        ip = #{ip}
 WHERE board_type_seq = #{boardTypeSeq}
   AND board_seq = #{boardSeq}
   AND member_id = #{memberId}

mapper.delete:
DELETE FROM board_vote
 WHERE board_type_seq = #{boardTypeSeq}
   AND board_seq = #{boardSeq}
   AND member_id = #{memberId}
